function(doc) { 
                if (doc.seeds) { 
                    var d = new Document();
                    for (var i=0; i<doc.seeds.length; i++) { 
                        d.add(doc.seeds[i], {"field": "seed", "analyzer": "keyword"});
                    }
                    d.add(doc.last_modified.value, {"field": "last_modified", "type": "string", "index": "not_analyzed", "store": "yes"});
                    return d;
                }
                return null;
            }
